mapper.select:
SELECT id AS id, head AS head, publisher AS publisher, role_id AS roleId, subject AS subject, content AS content, files AS files, pubdate AS pubdate FROM ieps_inform WHERE subject IN
#{subject}
ORDER BY pubdate DESC

mapper.delete:
DELETE FROM ieps_inform
WHERE id IN

    #{id}

mapper.select:
SELECT id, head, publisher, roleId, subject, content, files, pubdate FROM ( SELECT ii.id AS id, ii.head AS head, ii.publisher AS publisher, ii.role_id AS roleId, ii.subject AS subject, ii.content AS content, ii.files AS files, ii.pubdate AS pubdate FROM ieps_inform ii WHERE ii.subject IN
#{subject}
) ii
OR ii.head LIKE '%${inform.head}%'
OR ii.subject LIKE '%${inform.subject}%'
ORDER BY ii.pubdate DESC
